import React, { Component, useState } from 'react';
import { connect } from 'react-redux';
import * as actions from '../actions/userInfo';
import Header from '../components/Header';
import Product from './product';


import styles from './detail.module.css';
import Container from 'react-bootstrap/Container'
import Row from 'react-bootstrap/Row'
import Col from 'react-bootstrap/Col'



import Typography from '@material-ui/core/Typography';
import Slider from '@material-ui/core/Slider';

import Breadcrumbs from '@material-ui/core/Breadcrumbs';
import Link from '@material-ui/core/Link';




class Detail extends Component {

  render() {
    return (
      <div>
        <Header></Header>

        <div className={styles.tab}>

        </div>

        <div>
          <Container className={styles.containers}>
            <Row className={styles.row_t}>
              <Col
                className={styles.col_right}
              >

                <Container className={styles.container_right}>
                  <Row className={styles.row1_r}>
                    <Breadcrumbs aria-label="breadcrumb">
                      <Link color="rgb(52, 92, 115)" href="/">
                        Mechanical
                      </Link>
                      <Typography color="inherit">HVAC Fans</Typography>
                    </Breadcrumbs>
                  </Row>

                  <Row className={styles.row2_r}>
                    <Container className={styles.container_row2_r}>
                      {this.props.selectedFan && (
                        <div className={styles.fan_div}>
                          <div className={styles.fan_class1}>
                            <img className={styles.fan_image} src={this.props.selectedFan.fan_image}></img>
                            <p>{this.props.selectedFan.manufacturer}</p>
                            <p>{this.props.selectedFan.series}</p>
                            <p>{this.props.selectedFan.model}</p>
                          </div>
                          <div className={styles.fan_class2}>
                            <p>{this.props.selectedFan.airflow} CFM</p>
                            <p>{this.props.selectedFan.power_max}W at max speed</p>
                            <p>{this.props.selectedFan.sound_at_max_speed}dBA sound_at_max_speed</p>
                            <p>{this.props.selectedFan.fan_sweep_diameter}"fan sweep diameter</p>
                          </div >
                          <div className={styles.fan_class3}>
                            <p>Past specifications:</p>
                            <p>{this.props.selectedFan.firm} firm </p>
                            <p>{this.props.selectedFan.global} global</p>

                          </div>
                        </div>
                      )}

                    </Container>
                  </Row>
                </Container>

              </Col>
            </Row>

          </Container>
        </div>

      </div>)
      ;
  };
}


const mapStateToProps = state => {
  return {
    loading: state.auth.loading,
    error: state.auth.error,
    isAuthenticated: state.auth.token !== null,


    selectedFan: state.auth.selectedFan
  };
};

const mapDispatchToProps = dispatch => {
  return {
    getHomePage: token => dispatch(actions.getHomePage(token))
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(Detail);
