import React, { Component } from 'react';
import { connect } from 'react-redux';
import * as actions from '../actions/userInfo';
import styles from './dashboard.module.css';

import jooleLogo from '../assets/logo.png';
import PopUp from '../components/PopUp';

class Dashboard extends Component {

    userName = localStorage.getItem("username");
    url_image = localStorage.getItem("image");

    displayHomePage = () => {
        this.props.getHomePage(localStorage.getItem("token"));
    }


    state = {
        seen: false
    };

    togglePop = () => {
        this.setState({
            seen: !this.state.seen
        });
    };

    render() {
        return (
            <div className={styles.background}>
                <div>{this.userName}</div>
                <div>
                    <div className={styles.btn} onClick={this.togglePop}>
                        <input className={styles.image} type="image" src="https://www.rogersandcowan.com/wp-content/uploads/2018/12/Kobe-Bryant-300x270.jpg" />
                    </div>
                    {this.state.seen ? <PopUp toggle={this.togglePop} /> : null}
                </div>

                <input type='submit' value='Sign up' className={styles.flat} onClick={this.goToSignUp} />

                <span>
                    <img className={styles.logo} src={jooleLogo} alt="Joole" />
                </span>


                <br />

                <span className={styles.title}>
                    <h2> Building Product Selection Platform </h2>
                </span>

                <form className={styles.form} onSubmit={this.submitHandler}>
                    <input className={styles.input_username}
                        name='username'
                        placeholder='Username or Email'
                        onChange={this.inputChangedHandler}
                    />
                    <br />

                    <input className={styles.input_password}
                        type='password'
                        name='password'
                        placeholder='Password'
                        onChange={this.inputChangedHandler}
                    />
                    <br />

                    <input className={styles.input_submit}
                        type='submit'
                        value='Log in'
                    />
                </form>




            </div>)
            ;
    };
}


const mapStateToProps = state => {
    return {
        loading: state.auth.loading,
        error: state.auth.error,
        isAuthenticated: state.auth.token !== null,

    };
};

const mapDispatchToProps = dispatch => {
    return {
        getHomePage: token => dispatch(actions.getHomePage(token))
    };
};

export default connect(mapStateToProps, mapDispatchToProps)(Dashboard);
