import React, { Component } from 'react';
import { connect } from 'react-redux';
import * as actions from '../actions/userInfo';
import Header from '../components/Header';

import styles from './product.module.css';
import Container from 'react-bootstrap/Container'
import Row from 'react-bootstrap/Row'
import Col from 'react-bootstrap/Col'


class Product extends Component {

  displayHomePage = () => {
    this.props.getHomePage(localStorage.getItem("token"));
  }


  constructor(propss) {
    super(propss);
    this.state = {
      hide: false,
      disable: false
    };
    this.toggleButton = this.toggleButton.bind(this);
    this.disableButton = this.disableButton.bind(this);
  }
  toggleButton(value) {
    this.setState({ hide: value });
  }
  disableButton() {
    this.setState({ disable: true });
  }

  render() {
    return (
      <div>
        <Header></Header>
        <div>
          <Container className={styles.containers}>
            <Row className={styles.row_t}>
              <Col
                className={styles.col_left}
                xs={3}>
                <Container className={styles.container_left}>
                  <Row className={styles.row1_l}>
                    <Col>
                      <h6 className={styles.text}>Search:</h6>
                    </Col>
                    <Col>
                      <button className={styles.button}>Save</button>
                    </Col>
                    <Col>
                      <button className={styles.button}>Clear</button>
                    </Col>
                  </Row>
                  <Row className={styles.row2_l}>
                    <Col className={styles.col_r2_l}>Product</Col>
                    <Col>Project</Col>
                  </Row>
                </Container>
              </Col>
              <Col
                className={styles.col_right}
                xs={9}>
                <div>
        {!this.state.hide && 
          <button onClick={() => this.toggleButton(true)}>Hide</button>
        }
        <button onClick={this.disableButton} disabled={this.state.disable}>
          disable
        </button>

      {(this.state.hide || this.state.disable) && (
      <button style={{ background: "green", color: "white" }}>
        New button
      </button>
    )}
      </div>
                </Col>
            </Row>

          </Container>
        </div>

      </div>)
      ;
  };
}


const mapStateToProps = state => {
  return {
    loading: state.auth.loading,
    error: state.auth.error,
    isAuthenticated: state.auth.token !== null,

  };
};

const mapDispatchToProps = dispatch => {
  return {
    getHomePage: token => dispatch(actions.getHomePage(token))
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(Product);
